import React from 'react'
import { useDrop } from 'react-dnd'
const style = {
  border: '1px dashed gray',
  width: '100%',
  height: '200px',
  marginRight: '1.5rem',
  marginBottom: '1.5rem',
  color: '#222',
  padding: '1rem',
  textAlign: 'center',
  fontSize: '1rem',
  lineHeight: 'normal',
  float: 'left',
}
const Dustbin = ({ accept, lastDroppedItem, onDrop, droppedBoxNames}) => {
  const [{ isOver, canDrop }, drop] = useDrop({
    accept,
    drop: onDrop,
    collect: monitor => ({
      isOver: monitor.isOver(),
      canDrop: monitor.canDrop(),
    }),
  })
  console.log(lastDroppedItem)
  console.log(droppedBoxNames)
  const isActive = isOver && canDrop
  let backgroundColor = 'white'
  if (isActive) {
    backgroundColor = 'darkgreen'
  } else if (canDrop) {
    backgroundColor = 'darkkhaki'
  }
  return (
    <div ref={drop} style={{ ...style, backgroundColor }}>
      {isActive
        ? 'Release to drop'
        : `This dustbin accepts: ${accept.join(', ')}`}

      {lastDroppedItem && (
        <div>
          for (var i = droppedBoxNames.length - 1; i >= 0; i--) {
            Things[i]
          }
          <button name={lastDroppedItem.name} class={lastDroppedItem.classeName} dangerouslySetInnerHTML={{__html: lastDroppedItem.html}}>
          </button>
        </div>
      )}
    </div>
  )
}
export default Dustbin
