import React, { Component } from 'react';
import { isAuthenticated } from '../services/auth'; 
import { Redirect } from 'react-router'
import Footer from '../components/Footer'; 

import Example from '../components/plugin/example';
import { DndProvider } from 'react-dnd'
import HTML5Backend from 'react-dnd-html5-backend'

import api from "../services/api";

import "../components/Action.css" 

class Actions extends Component { 
    constructor(props){ 
        super(props);
        this.id = this.props.location.search.replace("?", "");
        console.log(this.id);
    }

    populateDevices = async () => {
        const request = await api.post('/authorization/'+this.id);
        console.log(request); 
        if(request !== undefined){
            this.setState({devices: request.data.devices}); 
            // console.log(this.state.devices); 
        }
    }

    async componentDidMount() {

        if (isAuthenticated()) {
            document.title = "Ações";
            await this.populateDevices(); 
        }
    }

    render() {
        return ( 
            <div >
                { (!isAuthenticated() ) ? 
                    <Redirect to="/login"/> : 
                    <div className="container">
                        <div className="card-body">
                            <div className="row">
                                <button type="button" className="btn btn-info">
                                    <i className="fa fa-play" aria-hidden="true"></i>
                                </button>
                            </div>
                            <div className="row">
                                <DndProvider backend={HTML5Backend}>
                                    <Example/>
                                </DndProvider>
                            </div>
                        </div>
                    </div>
                }
                <Footer></Footer>
            </div>
        );
    }
}

export default Actions;
