import { useDrag } from 'react-dnd'
const style = {
  border: '1px dashed gray',
  backgroundColor: 'white',
  padding: '0.5rem 1rem',
  marginRight: '1.5rem',
  marginBottom: '1.5rem',
  cursor: 'move',
  float: 'left',
}
const Box = ({ classeName, html, type, isDropped }) => {
  const [{ opacity }, drag] = useDrag({
    item: { html, type },
    collect: monitor => ({
      opacity: monitor.isDragging() ? 0.4 : 1,
    }),
  })
  return (
    <button class={classe} ref={drag} style={{ ...style, opacity }} dangerouslySetInnerHTML={{__html: html}}>
