import React from 'react'
import { useDrag } from 'react-dnd'
const Box = ({ name, classe, html, type, isDropped }) => {
  const [drag] = useDrag({
    item: { name, classe, html, type },
    collect: monitor => ({
      // opacity: monitor.isDragging() ? 0.4 : 1,
    }),
  })
  return (
    <button name={name} className={classe} ref={drag} 
    dangerouslySetInnerHTML={{__html: html}}>
    </button>
  )
}
export default Box
